get '/' do
  @nomes_count = nomes.count
  @apelidos_count = apelidos.count
  @nome1 = nomes.sample
  @nome2 = nomes.sample
  @apelido1 = apelidos.sample
  @apelido2 = apelidos.sample
  erb :index
end

get '/nome_aleatorio' do
  @nome1 = nomes.sample
  @nome2 = nomes.sample
  @apelido1 = apelidos.sample
  @apelido2 = apelidos.sample
  [@nome1, @nome2, @apelido1, @apelido2].to_json
end

get '/nome' do
  nomes.sample
end

get '/apelido' do
  apelidos.sample
end

get '/nomes', '/nomes/:number' do
  if params['number']
    number = params['number'].to_i
  else
    number = 10
  end
  nomes.shuffle[0,number].to_json
end

get '/apelidos', '/apelidos/:number' do
  if params['number']
    number = params['number'].to_i
  else
    number = 10
  end
  apelidos.shuffle[0,number].to_json
end


